mapper.update:
update emp
username = #{username}, name = #{name}, gender = #{gender}, image = #{image}, job = #{job}, entrydate = #{entrydate}, dept_id = #{deptId}, update_time = #{updateTime}
where id = #{id}

mapper.delete:
delete from emp
where id in

    #{id}

mapper.select:
# 调用通用代码

# concat()是MySQL的函数，用于字符串拼接

    name like concat('%', #{name}, '%')
    and gender = #{gender}

        and entrydate between #{start} and #{end}


order by update_time desc
